DROP TABLE IF EXISTS `physician_user`;
CREATE TABLE `physician_user`(physician_user_id int(11) NOT NULL AUTO_INCREMENT COMMENT '医师用户ID',
`doctors_name` varchar(64) comment '医师姓名',
`physician_gender` varchar(64) comment '医师性别',
`physicians_age` varchar(64) comment '医师年龄',
`examine_state` varchar(16) DEFAULT '已通过' NOT NULL comment '审核状态',
`user_id` int(11) DEFAULT '0' NOT NULL comment '用户ID',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (physician_user_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '医师用户';

DROP TABLE IF EXISTS `ordinary_user`;
CREATE TABLE `ordinary_user`(ordinary_user_id int(11) NOT NULL AUTO_INCREMENT COMMENT '普通用户ID',
`user_name` varchar(64) comment '用户姓名',
`user_gender` varchar(64) comment '用户性别',
`user_age` varchar(64) comment '用户年龄',
`examine_state` varchar(16) DEFAULT '已通过' NOT NULL comment '审核状态',
`user_id` int(11) DEFAULT '0' NOT NULL comment '用户ID',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (ordinary_user_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '普通用户';

DROP TABLE IF EXISTS `physician_information`;
CREATE TABLE `physician_information`(physician_information_id int(11) NOT NULL AUTO_INCREMENT COMMENT '医师信息ID',
`physician_user` int(11) DEFAULT 0 comment '医师用户',
`doctors_name` varchar(64) comment '医师姓名',
`visit_time` varchar(64) comment '就诊时间',
`duration_of_medical_practice` varchar(64) comment '从医时长',
`specializes_in_specific_fields` varchar(64) comment '擅长领域',
`personal_photo` varchar(255) comment '个人照片',
`personal_profile` longtext comment '个人简介',
`collect_len` int(11) DEFAULT 0 NOT NULL comment '收藏数',
`appointment_registration_limit_times` int(8) DEFAULT 0 NOT NULL comment '预约限制次数',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (physician_information_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '医师信息';

DROP TABLE IF EXISTS `appointment_registration`;
CREATE TABLE `appointment_registration`(appointment_registration_id int(11) NOT NULL AUTO_INCREMENT COMMENT '预约挂号ID',
`physician_user` int(11) DEFAULT 0 comment '医师用户',
`doctors_name` varchar(64) comment '医师姓名',
`visit_time` varchar(64) comment '就诊时间',
`duration_of_medical_practice` varchar(64) comment '从医时长',
`ordinary_user` int(11) DEFAULT 0 comment '普通用户',
`user_name` varchar(64) comment '用户姓名',
`appointment_time` datetime comment '预约时间',
`appointment_remarks` varchar(64) comment '预约备注',
`prescription_information_limit_times` int(8) DEFAULT 1 NOT NULL comment '开药限制次数',
`admission_information_limit_times` int(8) DEFAULT 1 NOT NULL comment '入院限制次数',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (appointment_registration_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '预约挂号';

DROP TABLE IF EXISTS `prescription_information`;
CREATE TABLE `prescription_information`(prescription_information_id int(11) NOT NULL AUTO_INCREMENT COMMENT '开药信息ID',
`physician_user` int(11) DEFAULT 0 comment '医师用户',
`doctors_name` varchar(64) comment '医师姓名',
`ordinary_user` int(11) DEFAULT 0 comment '普通用户',
`user_name` varchar(64) comment '用户姓名',
`appointment_time` datetime comment '预约时间',
`drug_content` text comment '开药内容',
`precautions` text comment '注意事项',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (prescription_information_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '开药信息';

DROP TABLE IF EXISTS `admission_information`;
CREATE TABLE `admission_information`(admission_information_id int(11) NOT NULL AUTO_INCREMENT COMMENT '入院信息ID',
`physician_user` int(11) DEFAULT 0 comment '医师用户',
`doctors_name` varchar(64) comment '医师姓名',
`visit_time` varchar(64) comment '就诊时间',
`duration_of_medical_practice` varchar(64) comment '从医时长',
`ordinary_user` int(11) DEFAULT 0 comment '普通用户',
`user_name` varchar(64) comment '用户姓名',
`admission_time` date comment '入院时间',
`reason_for_admission` varchar(64) comment '入院原因',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (admission_information_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '入院信息';

DROP TABLE IF EXISTS `classification_of_diseases`;
CREATE TABLE `classification_of_diseases`(classification_of_diseases_id int(11) NOT NULL AUTO_INCREMENT COMMENT '疾病分类ID',
`classification_of_diseases` varchar(64) comment '疾病分类',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (classification_of_diseases_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '疾病分类';

DROP TABLE IF EXISTS `disease_information`;
CREATE TABLE `disease_information`(disease_information_id int(11) NOT NULL AUTO_INCREMENT COMMENT '疾病信息ID',
`physician_user` int(11) DEFAULT 0 comment '医师用户',
`name_of_disease` varchar(64) comment '疾病名称',
`classification_of_diseases` varchar(64) comment '疾病分类',
`disease_symptoms` varchar(64) comment '疾病症状',
`cause_of_disease` varchar(64) comment '疾病原因',
`inspection_department` varchar(64) comment '检查科室',
`cover_image` varchar(255) comment '封面图片',
`disease_details` longtext comment '疾病详情',
`collect_len` int(11) DEFAULT 0 NOT NULL comment '收藏数',
`create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
`update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
PRIMARY KEY (disease_information_id))ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 comment '疾病信息';

DROP TABLE IF EXISTS `drug_information`;
CREATE TABLE `drug_information`(drug_information_id int(11) NOT NULL AUTO_INCREMENT COMMENT '药品信息ID',
`physician_user` int(11) DEFAULT 0 comment '医师用户',
`drug_name` varchar(64) comment '药品名称',
`type_of_drug` varchar(64) comment '药品类型',
`drug_efficacy` varchar(64) comment '药品功效',
`usage_and_dosage` varchar(64) comment '用法用量',
`applicable_diseases` varchar(64) comment '适用疾病',
`precautions` varchar(64) comment '注意事项',
`pictures_of_medicines` varchar(255) comment '药品图片',
`drug_introduction` longtext comment '药品简介',
